import './App.css';
import Header from "./common/header/Header"
// This is a React Router v5 app
// import { BrowserRouter as Router, Route } from "react-router-dom"
// import { BrowserRouter as  } from "react-router-dom"
// import {
//   BrowserRouter,
//   Routes,
//   Route,
//   Link,
// } from "react-router-dom";
// import { BrowserRouter as Router, Route } from "react-router-dom"
import { Pages } from "./pages/Pages"
import Data from './components/flashDeals/Data';
import { useState } from 'react';
import Cart from './common/cart/Cart';
import { Home } from './components/mainpage/Home';
import { BrowserRouter as Router, Route } from "react-router-dom";

// import { Switch } from "react-router";
function App() {
  // step 1: fetch 
  const {productItems} = Data;

  const [cartItem, setCardItem] = useState([])

  const addToCart = (product) =>{
    const productExit = cartItem.find((item) => item.id === product.id )
    if (productExit) {
      setCardItem(cartItem.map((item) =>
      (item.id === product.id ? { ...productExit, qty:productExit.qty+1 } : item )))
    }
  }
  return ( 
    <>

      <Router>  
      <Header></Header>      
          {/* <Pages productItems={productItems} addToCart={addToCart} /> */}
          {/* <Route path="/" element={<Pages productItems={productItems} addToCart={addToCart} />} /> */}
          <Pages path="/" productItems={productItems} addToCart={addToCart} />
          <Cart path="/cart" cartItem={cartItem} addToCart={addToCart} />
        </Router>
    </>

  );
}


